const require_utils = require('../../../common/utils.cjs');

//#region src/feeds/json/generate/utils.ts
const generateItem = (item) => {
	return require_utils.trimObject({
		...item,
		date_published: require_utils.generateRfc3339Date(item?.date_published),
		date_modified: require_utils.generateRfc3339Date(item?.date_modified)
	});
};
const generateFeed = (feed) => {
	return require_utils.trimObject({
		version: "https://jsonfeed.org/version/1.1",
		...feed,
		items: require_utils.trimArray(feed?.items, generateItem)
	});
};

//#endregion
exports.generateFeed = generateFeed;