const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/app/generate/utils.ts
const generateControl = (control) => {
	if (!require_utils.isObject(control)) return;
	return require_utils.trimObject({ "app:draft": require_utils.generateYesNoBoolean(control.draft) });
};
const generateEntry = (entry) => {
	if (!require_utils.isObject(entry)) return;
	return require_utils.trimObject({
		"app:edited": require_utils.generateRfc3339Date(entry.edited),
		"app:control": generateControl(entry.control)
	});
};

//#endregion
exports.generateEntry = generateEntry;