import { generateRfc3339Date, generateYesNoBoolean, isObject, trimObject } from "../../../common/utils.js";

//#region src/namespaces/app/generate/utils.ts
const generateControl = (control) => {
	if (!isObject(control)) return;
	return trimObject({ "app:draft": generateYesNoBoolean(control.draft) });
};
const generateEntry = (entry) => {
	if (!isObject(entry)) return;
	return trimObject({
		"app:edited": generateRfc3339Date(entry.edited),
		"app:control": generateControl(entry.control)
	});
};

//#endregion
export { generateEntry };