const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/arxiv/generate/utils.ts
const generatePrimaryCategory = (primaryCategory) => {
	if (!require_utils.isObject(primaryCategory)) return;
	return require_utils.trimObject({
		"@term": require_utils.generatePlainString(primaryCategory.term),
		"@scheme": require_utils.generatePlainString(primaryCategory.scheme),
		"@label": require_utils.generatePlainString(primaryCategory.label)
	});
};
const generateAuthor = (author) => {
	if (!require_utils.isObject(author)) return;
	return require_utils.trimObject({ "arxiv:affiliation": require_utils.generateCdataString(author.affiliation) });
};
const generateEntry = (entry) => {
	if (!require_utils.isObject(entry)) return;
	return require_utils.trimObject({
		"arxiv:comment": require_utils.generateCdataString(entry.comment),
		"arxiv:journal_ref": require_utils.generateCdataString(entry.journalRef),
		"arxiv:doi": require_utils.generateCdataString(entry.doi),
		"arxiv:primary_category": generatePrimaryCategory(entry.primaryCategory)
	});
};

//#endregion
exports.generateAuthor = generateAuthor;
exports.generateEntry = generateEntry;