import { generateCdataString, generatePlainString, isObject, trimObject } from "../../../common/utils.js";

//#region src/namespaces/arxiv/generate/utils.ts
const generatePrimaryCategory = (primaryCategory) => {
	if (!isObject(primaryCategory)) return;
	return trimObject({
		"@term": generatePlainString(primaryCategory.term),
		"@scheme": generatePlainString(primaryCategory.scheme),
		"@label": generatePlainString(primaryCategory.label)
	});
};
const generateAuthor = (author) => {
	if (!isObject(author)) return;
	return trimObject({ "arxiv:affiliation": generateCdataString(author.affiliation) });
};
const generateEntry = (entry) => {
	if (!isObject(entry)) return;
	return trimObject({
		"arxiv:comment": generateCdataString(entry.comment),
		"arxiv:journal_ref": generateCdataString(entry.journalRef),
		"arxiv:doi": generateCdataString(entry.doi),
		"arxiv:primary_category": generatePrimaryCategory(entry.primaryCategory)
	});
};

//#endregion
export { generateAuthor, generateEntry };