const require_utils = require('../../../feeds/atom/generate/utils.cjs');

//#region src/namespaces/atom/generate/utils.ts
const generateEntry = (entry) => {
	return require_utils.generateEntry(entry, {
		prefix: "atom:",
		asNamespace: true
	});
};
const generateFeed = (feed) => {
	return require_utils.generateFeed(feed, {
		prefix: "atom:",
		asNamespace: true
	})?.feed;
};

//#endregion
exports.generateEntry = generateEntry;
exports.generateFeed = generateFeed;