const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/cc/generate/utils.ts
const generateItemOrFeed = (data) => {
	if (!require_utils.isObject(data)) return;
	return require_utils.trimObject({
		"cc:license": require_utils.generateCdataString(data.license),
		"cc:morePermissions": require_utils.generateCdataString(data.morePermissions),
		"cc:attributionName": require_utils.generateCdataString(data.attributionName),
		"cc:attributionURL": require_utils.generateCdataString(data.attributionURL),
		"cc:useGuidelines": require_utils.generateCdataString(data.useGuidelines),
		"cc:permits": require_utils.generateCdataString(data.permits),
		"cc:requires": require_utils.generateCdataString(data.requires),
		"cc:prohibits": require_utils.generateCdataString(data.prohibits),
		"cc:jurisdiction": require_utils.generateCdataString(data.jurisdiction),
		"cc:legalcode": require_utils.generateCdataString(data.legalcode),
		"cc:deprecatedOn": require_utils.generateCdataString(data.deprecatedOn)
	});
};

//#endregion
exports.generateItemOrFeed = generateItemOrFeed;