const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/content/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({ "content:encoded": require_utils.generateCdataString(item?.encoded) });
};

//#endregion
exports.generateItem = generateItem;