const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/creativecommons/generate/utils.ts
const generateItemOrFeed = (itemOrFeed) => {
	if (!require_utils.isObject(itemOrFeed)) return;
	return require_utils.trimObject({ "creativeCommons:license": require_utils.trimArray(itemOrFeed.licenses, require_utils.generateCdataString) });
};

//#endregion
exports.generateItemOrFeed = generateItemOrFeed;