const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/feedpress/generate/utils.ts
const generateFeed = (feed) => {
	if (!require_utils.isObject(feed)) return;
	return require_utils.trimObject({
		"feedpress:link": require_utils.generateCdataString(feed.link),
		"feedpress:newsletterId": require_utils.generateCdataString(feed.newsletterId),
		"feedpress:locale": require_utils.generateCdataString(feed.locale),
		"feedpress:podcastId": require_utils.generateCdataString(feed.podcastId),
		"feedpress:cssFile": require_utils.generateCdataString(feed.cssFile)
	});
};

//#endregion
exports.generateFeed = generateFeed;