const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/pingback/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"pingback:server": require_utils.generateCdataString(item.server),
		"pingback:target": require_utils.generateCdataString(item.target)
	});
};
const generateFeed = (feed) => {
	if (!require_utils.isObject(feed)) return;
	return require_utils.trimObject({ "pingback:to": require_utils.generateCdataString(feed.to) });
};

//#endregion
exports.generateFeed = generateFeed;
exports.generateItem = generateItem;