import { generateCdataString, isObject, trimObject } from "../../../common/utils.js";

//#region src/namespaces/pingback/generate/utils.ts
const generateItem = (item) => {
	if (!isObject(item)) return;
	return trimObject({
		"pingback:server": generateCdataString(item.server),
		"pingback:target": generateCdataString(item.target)
	});
};
const generateFeed = (feed) => {
	if (!isObject(feed)) return;
	return trimObject({ "pingback:to": generateCdataString(feed.to) });
};

//#endregion
export { generateFeed, generateItem };