const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/psc/generate/utils.ts
const generateChapter = (chapter) => {
	if (!require_utils.isObject(chapter)) return;
	return require_utils.trimObject({
		"@start": require_utils.generatePlainString(chapter.start),
		"@title": require_utils.generatePlainString(chapter.title),
		"@href": require_utils.generatePlainString(chapter.href),
		"@image": require_utils.generatePlainString(chapter.image)
	});
};
const generateChapters = (chapters) => {
	return require_utils.trimObject({ "psc:chapter": require_utils.trimArray(chapters, generateChapter) });
};
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({ "psc:chapters": generateChapters(item.chapters) });
};

//#endregion
exports.generateItem = generateItem;