import { generatePlainString, isObject, trimArray, trimObject } from "../../../common/utils.js";

//#region src/namespaces/psc/generate/utils.ts
const generateChapter = (chapter) => {
	if (!isObject(chapter)) return;
	return trimObject({
		"@start": generatePlainString(chapter.start),
		"@title": generatePlainString(chapter.title),
		"@href": generatePlainString(chapter.href),
		"@image": generatePlainString(chapter.image)
	});
};
const generateChapters = (chapters) => {
	return trimObject({ "psc:chapter": trimArray(chapters, generateChapter) });
};
const generateItem = (item) => {
	if (!isObject(item)) return;
	return trimObject({ "psc:chapters": generateChapters(item.chapters) });
};

//#endregion
export { generateItem };