const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/psc/parse/utils.ts
const parseChapter = (value) => {
	if (!require_utils.isObject(value)) return;
	return require_utils.trimObject({
		start: require_utils.parseSingularOf(value["@start"], require_utils.parseString),
		title: require_utils.parseSingularOf(value["@title"], require_utils.parseString),
		href: require_utils.parseSingularOf(value["@href"], require_utils.parseString),
		image: require_utils.parseSingularOf(value["@image"], require_utils.parseString)
	});
};
const parseChapters = (value) => {
	return require_utils.parseArrayOf(value?.["psc:chapter"], parseChapter);
};
const retrieveItem = (value) => {
	if (!require_utils.isObject(value)) return;
	return require_utils.trimObject({ chapters: require_utils.parseSingularOf(value["psc:chapters"], parseChapters) });
};

//#endregion
exports.retrieveItem = retrieveItem;