const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/slash/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"slash:section": require_utils.generateCdataString(item.section),
		"slash:department": require_utils.generateCdataString(item.department),
		"slash:comments": require_utils.generateNumber(item.comments),
		"slash:hit_parade": require_utils.generateCsvOf(item.hitParade, require_utils.generateNumber)
	});
};

//#endregion
exports.generateItem = generateItem;