import { generateCdataString, generateNumber, generatePlainString, isObject, trimObject } from "../../../common/utils.js";

//#region src/namespaces/spotify/generate/utils.ts
const generateLimit = (limit) => {
	if (!isObject(limit)) return;
	return trimObject({ "@recentCount": generateNumber(limit.recentCount) });
};
const generatePartner = (partner) => {
	if (!isObject(partner)) return;
	return trimObject({ "@id": generatePlainString(partner.id) });
};
const generateSandbox = (sandbox) => {
	if (!isObject(sandbox)) return;
	return { "@enabled": sandbox.enabled };
};
const generateFeedAccess = (access) => {
	if (!isObject(access)) return;
	return trimObject({
		partner: generatePartner(access.partner),
		sandbox: generateSandbox(access.sandbox)
	});
};
const generateEntitlement = (entitlement) => {
	if (!isObject(entitlement)) return;
	return trimObject({ "@name": generatePlainString(entitlement.name) });
};
const generateItemAccess = (access) => {
	if (!isObject(access)) return;
	return trimObject({ entitlement: generateEntitlement(access.entitlement) });
};
const generateFeed = (feed) => {
	if (!isObject(feed)) return;
	return trimObject({
		"spotify:limit": generateLimit(feed.limit),
		"spotify:countryOfOrigin": generateCdataString(feed.countryOfOrigin),
		"spotify:access": generateFeedAccess(feed.access)
	});
};
const generateItem = (item) => {
	if (!isObject(item)) return;
	return trimObject({ "spotify:access": generateItemAccess(item.access) });
};

//#endregion
export { generateFeed, generateItem };