const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/sy/generate/utils.ts
const generateFeed = (feed) => {
	if (!require_utils.isObject(feed)) return;
	return require_utils.trimObject({
		"sy:updatePeriod": require_utils.generateCdataString(feed.updatePeriod),
		"sy:updateFrequency": require_utils.generateNumber(feed.updateFrequency),
		"sy:updateBase": require_utils.generateRfc3339Date(feed.updateBase)
	});
};

//#endregion
exports.generateFeed = generateFeed;