const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/thr/generate/utils.ts
const generateInReplyTo = (inReplyTo) => {
	if (!require_utils.isObject(inReplyTo)) return;
	return require_utils.trimObject({
		"@ref": require_utils.generatePlainString(inReplyTo.ref),
		"@href": require_utils.generatePlainString(inReplyTo.href),
		"@type": require_utils.generatePlainString(inReplyTo.type),
		"@source": require_utils.generatePlainString(inReplyTo.source)
	});
};
const generateLink = (link) => {
	if (!require_utils.isObject(link)) return;
	return require_utils.trimObject({
		"@thr:count": require_utils.generateNumber(link.count),
		"@thr:updated": require_utils.generateRfc3339Date(link.updated)
	});
};
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"thr:total": require_utils.generateNumber(item.total),
		"thr:in-reply-to": require_utils.trimArray(item.inReplyTos, generateInReplyTo)
	});
};

//#endregion
exports.generateItem = generateItem;
exports.generateLink = generateLink;