const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/trackback/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"trackback:ping": require_utils.generateCdataString(item.ping),
		"trackback:about": require_utils.trimArray(item.abouts, require_utils.generateCdataString)
	});
};

//#endregion
exports.generateItem = generateItem;