const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/wfw/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"wfw:comment": require_utils.generateCdataString(item.comment),
		"wfw:commentRss": require_utils.generateCdataString(item.commentRss)
	});
};

//#endregion
exports.generateItem = generateItem;