const require_utils = require('../../../common/utils.cjs');

//#region src/namespaces/yt/generate/utils.ts
const generateItem = (item) => {
	if (!require_utils.isObject(item)) return;
	return require_utils.trimObject({
		"yt:videoId": require_utils.generateCdataString(item.videoId),
		"yt:channelId": require_utils.generateCdataString(item.channelId)
	});
};
const generateFeed = (feed) => {
	if (!require_utils.isObject(feed)) return;
	return require_utils.trimObject({
		"yt:channelId": require_utils.generateCdataString(feed.channelId),
		"yt:playlistId": require_utils.generateCdataString(feed.playlistId)
	});
};

//#endregion
exports.generateFeed = generateFeed;
exports.generateItem = generateItem;