const require_config = require('../../common/config.cjs');
let fast_xml_parser = require("fast-xml-parser");

//#region src/opml/parse/config.ts
const stopNodes = [
	"opml.head.title",
	"opml.head.dateCreated",
	"opml.head.dateModified",
	"opml.head.ownerName",
	"opml.head.ownerEmail",
	"opml.head.ownerId",
	"opml.head.docs",
	"opml.head.expansionState",
	"opml.head.vertScrollState",
	"opml.head.windowTop",
	"opml.head.windowLeft",
	"opml.head.windowBottom",
	"opml.head.windowRight",
	"*.outline.outline"
];
const parser = new fast_xml_parser.XMLParser({
	...require_config.parserConfig,
	stopNodes
});

//#endregion
exports.parser = parser;