import { locales } from "../../common/config.js";
import { parser } from "./config.js";
import { parseDocument } from "./utils.js";

//#region src/opml/parse/index.ts
const parse = (value, options) => {
	const parsed = parseDocument(parser.parse(value), options);
	if (!parsed) throw new Error(locales.invalidOpmlFormat);
	return parsed;
};

//#endregion
export { parse };